import { makeStyles } from 'tss-react/mui';
import { Grid2, Button } from '@mui/material';
import atmButton from "../assets/atm-button.png";
import ccSprite from "../assets/creditcard_sprite.png";


const useStyles = makeStyles()((theme) => ({
    item: {
        backgroundColor: "red",
        height: "100%",
    },
    atmButton: {
        backgroundColor: "lightgray",
        height: "30px"
    },
    atmSignShadow: {
        backgroundColor: "lightgray",
        height: "0.5em",
        padding: 0,
        width: "100%"
    },
}));


function DisplayScreen() {
    const { classes } = useStyles();

    return (
        <Grid2 style={{ padding: 0, background: "white", justifyContent: "center", width: "60%" }} container>
            <div className={classes.atmSignShadow} />
            <Grid2>
                <img src={ccSprite} alt="ccSprite" style={{ padding: "5px 0 0 0" }} />
            </Grid2>
            <Grid2>

            </Grid2>

        </Grid2>
    );
}

export default DisplayScreen;
