import { makeStyles } from 'tss-react/mui';
import { Grid2, Button } from '@mui/material';
import atmButton from "../assets/atm-button.png";
import ccSprite from "../assets/creditcard_sprite.png";


const useStyles = makeStyles()((theme) => ({
    item: {
        backgroundColor: "red",
        height: "100%",
    },
    atmButton: {
        backgroundColor: "lightgray",
        height: "30px",
        width: "20px"
    },
    atmSignShadow: {
        backgroundColor: "lightgray",
        height: "0.5em",
        padding: 0,
        width: "100%"
    },
}));


function DisplayScreen() {
    const { classes } = useStyles();

    return (
        <Grid2 style={{ padding: 0, background: "white", justifyContent: "center", width: "60%" }} container>
            <div className={classes.atmSignShadow} />
            <Grid2>
                <img src={ccSprite} alt="ccSprite" style={{ padding: "5px 0 0 0" }} />
            </Grid2>
            <Grid2 direction="row" style={{ backgroundColor: "red", width: "100%" }} container>
                <Grid2 style={{ backgroundColor: "yellow", direction: "column", width: "55px" }}>
                    <Grid2>
                        {/* <Button className={classes.atmButton} /> */}
                        {/* <div style={{ height: "5px", width: "20px", backgroundColor: "gray", display: "inline-block" }} /> */}
                    </Grid2>
                    <Grid2>
                        <Button>b2</Button>
                    </Grid2>
                    <Grid2>
                        <Button>b3</Button>
                    </Grid2>
                    <Grid2>
                        <Button>b4</Button>
                    </Grid2>
                </Grid2>
                <Grid2 style={{ backgroundColor: "pink", direction: "column"  }}>
                    <div style={{ padding: "5px 0 5px 0", justifyItems: "center", backgroundColor: "lightgray", width: "250px" }}>
                        <div style={{ backgroundColor: "white", width: "240px" }}>
                            test
                        </div>
                    </div>
                </Grid2>
                <Grid2 style={{backgroundColor: "purple", direction: "column", width: "55px" }}>
                    <Grid2>
                        <Button>b1</Button>
                    </Grid2>
                    <Grid2>
                        <Button>b2</Button>
                    </Grid2>
                    <Grid2>
                        <Button>b3</Button>
                    </Grid2>
                    <Grid2>
                        <Button>b4</Button>
                    </Grid2>
                </Grid2>
            </Grid2>
        </Grid2>
    );
}

export default DisplayScreen;
