import atmSign from "../assets/atm_sign.png";
import ccSprite from "../assets/creditcard_sprite.png";

import { makeStyles } from 'tss-react/mui';


const useStyles = makeStyles()((theme) => ({
    atmSignShadow: {
        backgroundColor: "lightgray",
        height: "0.5em",
        width: "100%",
        padding: 0,
        margin: "-3px 0 0 0"
    }
}));

function DisplayHeader() {
    const { classes } = useStyles();

    return (
        <div style={{justifyItems: "center", backgroundColor}}>
            <img src={atmSign} alt="atmSign" />
            <div className={classes.atmSignShadow} />
            <img src={ccSprite} alt="ccSprite" style={{padding: "5px 0 0 0"}}/>
        </div>
    )
}

export default DisplayHeader;
