const theme = createTheme({
  components: {
    MuiBox: {
      defaultProps: {
        sx: {
          // Define default dimensions for Box components
          width: '100%',
          maxWidth: '1200px',
          margin: '0 auto',
          padding: (theme) => theme.spacing(2),
        },
      },
    },
    MuiContainer: {
        defaultProps: {
          maxWidth: 'lg', // or 'md', 'sm', 'xl'
          sx: {
            padding: (theme) => theme.spacing(2),
          },
        },
      },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <Box>
        {/* App content here */}
      </Box>
    </ThemeProvider>
  );
}

